import java.util.Scanner;

public class sum {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int s1=sc.nextInt();
		int s2=sc.nextInt();
		int sum=s1+s2;
		System.out.println("The sum of"+" "+s1+" "+"and"+" "+s2+" "+"is"+" "+sum);
	}

}
